import AdminDashboard from './AdminDashboard.vue';
import 'bootstrap/dist/css/bootstrap.css';
export default {
  name: 'ComponentFour',
  components: {
    AdminDashboard
  },
  data() {
    return {
      showPasswordInPlainText: false,
      username: '',
      password: '',
      authenticated: false,
      errMessage: ''
    };
  },
  methods: {
    login() {
      // Send a POST request to the API for validation
      fetch('trash/api_user.php/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(this.credential)
      }).then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Authentication failed');
        }
      }).then(data => {
        console.log("Logon successful");
        this.authenticated = true;
      }).catch(error => {
        console.error('An error occurred during form validation:', error);
        this.errMessage = 'Please try again.';
        this.authenticated = false;
      });
      this.username = '';
      this.password = '';
    }
  },
  computed: {
    credential() {
      return {
        username: this.username,
        password: this.password
      };
    }
  }
};
